import styled from 'styled-components'

export const Container = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  height: 100%;
  justify-items: center;
  align-items: center;
  background-color: #ececec;
  padding: 30px;
`
export const Nav = styled.nav`
  position: fixed;
  display: flex;
  flex-direction: column;
  width: 100%;
  height: 60px;
  justify-items: center;
  align-items: center;
  background-color: #cf873d;
  padding: 5px;
`
