import React, {
  createContext,
  Dispatch,
  FunctionComponent,
  useContext,
  useReducer
} from 'react'

import { url } from '../helpers/constants/urls'
import { useAxios } from '../helpers/axios'
import { useForm } from './formUser'
import { getDefaultUser, IUser } from '../types'

const UserContext = createContext(null)

const initialState: {
  userList: IUser[]
  isValid: boolean
  status: string
} = {
  userList: [getDefaultUser()],
  isValid: false,
  status: 'create'
}

enum ActionTypes {
  GET_USER_LIST = 'GET_USER_LIST',
  STATUS = 'STATUS'
}

interface Action {
  name?: string
  value?: any
  user?: IUser
  isValid?: boolean
  userList?: IUser[]
  type: ActionTypes
}

function reducer(state: typeof initialState, action: Action) {
  switch (action.type) {
    case ActionTypes.GET_USER_LIST:
      return { ...state, userList: action.userList, error: false }
    case ActionTypes.STATUS:
      return { ...state, status: action.value }
    default:
      return state
  }
}

export function useUser() {
  const { apiClient, errorHandler } = useAxios()
  const { clearForm, validateUser, user, status, statusError } = useForm()

  const [{ userList, isValid }, dispatch] = useContext<
    [typeof initialState, Dispatch<Action>]
  >(UserContext)

  const getUsers = async (): Promise<void> => {
    try {
      const { data } = await apiClient.get<IUser[]>(url.USER_LIST)
      dispatch({ type: ActionTypes.GET_USER_LIST, userList: data })
    } catch (err) {
      statusError()
    }
  }

  const submitForm = () => {
    if (status === 'create') {
      return createUsers()
    } else {
      return udpateUser()
    }
  }

  const createUsers = async () => {
    if (validateUser) {
      try {
        await apiClient.post<IUser>(url.USER_LIST, user)
        await getUsers()
        await clearForm()
        return true
      } catch (err) {
        statusError()
        return false
      }
    }
  }

  const udpateUser = async () => {
    const { id } = user
    try {
      if (isValid) {
        await apiClient.patch(`${url.USER_LIST}/${id}`, user)
        await getUsers()
        await clearForm()
        return true
      }
    } catch (err) {
      errorHandler(err)
      return false
    }
  }

  return {
    userList,
    user,
    isValid,
    getUsers,
    validateUser,
    createUsers,
    submitForm
  }
}

const UserProvider: FunctionComponent = ({ children }) => {
  const [state, dispatch] = useReducer(reducer, initialState)

  return (
    <UserContext.Provider value={[state, dispatch]}>
      {children}
    </UserContext.Provider>
  )
}

export { UserContext, UserProvider }
